import React, { useState } from 'react';
import axios from 'axios';
import { Container, ListGroup, Form, Button } from 'react-bootstrap';

const TransactionsList = () => {
  const [userId, setUserId] = useState('');
  const [transactions, setTransactions] = useState([]);

  const getTransactions = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/transactions?user_id=${userId}`);
      setTransactions(response.data);
    } catch (error) {
      alert('Error al obtener transacciones');
    }
  };

  return (
    <Container>
      <h2>Historial de Transacciones</h2>
      <Form>
        <Form.Group>
          <Form.Label>User ID</Form.Label>
          <Form.Control type="text" value={userId} onChange={(e) => setUserId(e.target.value)} />
        </Form.Group>
        <Button variant="info" onClick={getTransactions}>Ver Transacciones</Button>
      </Form>
      <ListGroup>
        {transactions.map((txn) => (
          <ListGroup.Item key={txn.TransactionID}>
            {txn.Type} - {txn.FundID} - {txn.Amount}
          </ListGroup.Item>
        ))}
      </ListGroup>
    </Container>
  );
};

export default TransactionsList;
