import React, { useState } from 'react';
import axios from 'axios';

const SubscriptionForm = () => {
  const [userId, setUserId] = useState('');
  const [fundId, setFundId] = useState('');
  const [amount, setAmount] = useState('');

  const subscribe = async () => {
    try {
      const response = await axios.post('http://localhost:5000/subscribe', { user_id: userId, fund_id: fundId, amount });
      alert(`Suscripción exitosa: ${response.data.transaction_id}`);
    } catch (error) {
      alert(error.response.data.message);
    }
  };

  return (
    <div>
      <h2>Suscribirse a un Fondo</h2>
      <input placeholder="User ID" value={userId} onChange={(e) => setUserId(e.target.value)} />
      <input placeholder="Fund ID" value={fundId} onChange={(e) => setFundId(e.target.value)} />
      <input placeholder="Amount" value={amount} onChange={(e) => setAmount(e.target.value)} />
      <button onClick={subscribe}>Suscribirse</button>
    </div>
  );
};

export default SubscriptionForm;
