import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, ListGroup, Button, Alert } from 'react-bootstrap';
import log from 'loglevel';

log.setLevel('debug');

const TransactionsList = () => {
  const [transactions, setTransactions] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await axios.get('http://localhost:5000/all_transactions');
        log.debug('Transactions data:', response.data);  // Verifica la estructura de los datos aquí
        setTransactions(response.data);
      } catch (error) {
        log.error('Error fetching transactions:', error);
      }
    };

    fetchTransactions();
  }, []);

  const deleteTransaction = async (transactionId) => {
    try {
      await axios.delete(`http://localhost:5000/transaction/${transactionId}`);
      setTransactions(transactions.filter(txn => txn.TransaccionId !== transactionId));
      setMessage('Transacción eliminada con éxito.');
    } catch (error) {
      log.error('Error deleting transaction:', error);
      setMessage('Error eliminando la transacción.');
    }
  };

  return (
    <Container>
      <h2>Transacciones</h2>
      {message && <Alert variant="info">{message}</Alert>}
      <Alert variant="info">Cantidad de transacciones: {transactions.length}</Alert>
      <ListGroup>
        {transactions.map((txn) => (
          <ListGroup.Item key={txn.TransaccionId}>
            <div>
              <strong>Cliente ID:</strong> {txn.ClientID} <br />
              <strong>Tipo:</strong> {txn.Type} <br />
              <strong>Fondo ID:</strong> {txn.FundID} <br />
              <strong>Monto:</strong> {txn.Amount}
            </div>
            <Button
              variant="danger"
              onClick={() => deleteTransaction(txn.TransaccionId)}
              style={{ marginTop: '10px' }}
            >
              Eliminar Transacción
            </Button>
          </ListGroup.Item>
        ))}
      </ListGroup>
    </Container>
  );
};

export default TransactionsList;
