import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, ListGroup, Row, Col } from 'react-bootstrap';
import log from 'loglevel';

log.setLevel('debug');

const TransactionsList = () => {
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await axios.get('http://localhost:5000/all_transactions');
        log.debug('Transactions data:', response.data);  // Verifica la estructura de los datos aquí
        setTransactions(response.data);
      } catch (error) {
        log.error('Error fetching transactions:', error);
      }
    };

    fetchTransactions();
  }, []);

  return (
    <Container>
      <h2 className="mt-4 mb-4 text-center">Transacciones</h2>
      <ListGroup>
        {transactions.map((txn) => (
          <ListGroup.Item key={txn.TransaccionId} className="mb-2 shadow-sm p-3">
            <Row>
              <Col><strong>Cliente ID:</strong> {txn.ClientID}</Col>
              <Col><strong>Tipo:</strong> {txn.Type}</Col>
              <Col><strong>Fondo ID:</strong> {txn.FundID}</Col>
              <Col><strong>Monto:</strong> {txn.Amount}</Col>
            </Row>
          </ListGroup.Item>
        ))}
      </ListGroup>
    </Container>
  );
};

export default TransactionsList;
