import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, ListGroup } from 'react-bootstrap';

const AllTransactionsList = () => {
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await axios.get('http://localhost:5000/all_transactions');
        setTransactions(response.data);
      } catch (error) {
        console.error('Error fetching transactions:', error);
      }
    };

    fetchTransactions();
  }, []);

  return (
    <Container>
      <h2>Transacciones</h2>
      <ListGroup>
        {transactions.map((txn) => (
          <ListGroup.Item key={txn.TransaccionId.S}>
            Cliente ID: {txn.ClientID.S} - Fondo ID: {txn.FundID.N} - Monto: {txn.Amount.N}
          </ListGroup.Item>
        ))}
      </ListGroup>
    </Container>
  );
};

export default AllTransactionsList;
