import React, { useState } from 'react';
import axios from 'axios';
import { Form, Button, Container } from 'react-bootstrap';

const SuscriptionForm = () => {
  const [userId, setUserId] = useState('');
  const [fundId, setFundId] = useState('');
  const [amount, setAmount] = useState('');

  const subscribe = async () => {
    try {
      const response = await axios.post('http://localhost:5000/subscribe', { user_id: userId, fund_id: fundId, amount });
      alert(`Suscripción exitosa: ${response.data.transaction_id}`);
    } catch (error) {
      alert(error.response.data.message);
    }
  };

  return (
    <Container>
      <h2>Suscribirse a un Fondo</h2>
      <Form>
        <Form.Group>
          <Form.Label>User ID</Form.Label>
          <Form.Control type="text" value={userId} onChange={(e) => setUserId(e.target.value)} />
        </Form.Group>
        <Form.Group>
          <Form.Label>Fund ID</Form.Label>
          <Form.Control type="text" value={fundId} onChange={(e) => setFundId(e.target.value)} />
        </Form.Group>
        <Form.Group>
          <Form.Label>Amount</Form.Label>
          <Form.Control type="number" value={amount} onChange={(e) => setAmount(e.target.value)} />
        </Form.Group>
        <Button variant="primary" onClick={subscribe}>Suscribirse</Button>
      </Form>
    </Container>
  );
};

export default SuscriptionForm;
