import React, { useState } from 'react';
import axios from 'axios';

const UnsubscriptionForm = () => {
  const [userId, setUserId] = useState('');
  const [fundId, setFundId] = useState('');

  const unsubscribe = async () => {
    try {
      const response = await axios.post('http://localhost:5000/unsubscribe', { user_id: userId, fund_id: fundId });
      alert(`Cancelaci贸n exitosa: ${response.data.transaction_id}`);
    } catch (error) {
      alert(error.response.data.message);
    }
  };

  return (
    <div>
      <h2>Cancelar Suscripci贸n de un Fondo</h2>
      <input placeholder="User ID" value={userId} onChange={(e) => setUserId(e.target.value)} />
      <input placeholder="Fund ID" value={fundId} onChange={(e) => setFundId(e.target.value)} />
      <button onClick={unsubscribe}>Cancelar Suscripci贸n</button>
    </div>
  );
};

export default UnsubscriptionForm;
