import React, { useState } from 'react';
import axios from 'axios';
import { Form, Button, Container } from 'react-bootstrap';

const UnsubscriptionForm = () => {
  const [userId, setUserId] = useState('');
  const [fundId, setFundId] = useState('');

  const unsubscribe = async () => {
    try {
      const response = await axios.post('http://localhost:5000/unsubscribe', { user_id: userId, fund_id: fundId });
      alert(`Cancelaci贸n exitosa: ${response.data.transaction_id}`);
    } catch (error) {
      alert(error.response.data.message);
    }
  };

  return (
    <Container>
      <h2>Cancelar Suscripci贸n de un Fondo</h2>
      <Form>
        <Form.Group>
          <Form.Label>User ID</Form.Label>
          <Form.Control type="text" value={userId} onChange={(e) => setUserId(e.target.value)} />
        </Form.Group>
        <Form.Group>
          <Form.Label>Fund ID</Form.Label>
          <Form.Control type="text" value={fundId} onChange={(e) => setFundId(e.target.value)} />
        </Form.Group>
        <Button variant="danger" onClick={unsubscribe}>Cancelar Suscripci贸n</Button>
      </Form>
    </Container>
  );
};

export default UnsubscriptionForm;
