import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, ListGroup } from 'react-bootstrap';
import log from 'loglevel';

log.setLevel('debug');

const AllTransactionsList = () => {
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    const fetchTransactions = async () => {
      try {
        const response = await axios.get('http://localhost:5000/all_transactions');
        log.debug('Transactions data:', response.data);  // Verifica la estructura de los datos aquí
        setTransactions(response.data);
      } catch (error) {
        log.error('Error fetching transactions:', error);
      }
    };

    fetchTransactions();
  }, []);

  return (
    <Container>
      <h2>Transacciones</h2>
      <ListGroup>
        {transactions.map((txn) => (
          <ListGroup.Item key={txn.TransaccionId}>
            Cliente ID: {txn.ClientID} - {txn.Type} - Fondo ID: {txn.FundID} - Monto: {txn.Amount}
          </ListGroup.Item>
        ))}
      </ListGroup>
    </Container>
  );
};

export default AllTransactionsList;
