import React, { useState } from 'react';
import axios from 'axios';

const TransactionsList = () => {
  const [userId, setUserId] = useState('');
  const [transactions, setTransactions] = useState([]);

  const getTransactions = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/transactions?user_id=${userId}`);
      setTransactions(response.data);
    } catch (error) {
      alert('Error al obtener transacciones');
    }
  };

  return (
    <div>
      <h2>Historial de Transacciones</h2>
      <input placeholder="User ID" value={userId} onChange={(e) => setUserId(e.target.value)} />
      <button onClick={getTransactions}>Ver Transacciones</button>
      <ul>
        {transactions.map((txn) => (
          <li key={txn.TransactionID}>{txn.Type} - {txn.FundID} - {txn.Amount}</li>
        ))}
      </ul>
    </div>
  );
};

export default TransactionsList;
