import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, ListGroup } from 'react-bootstrap';
import log from 'loglevel';

log.setLevel('debug');

const ClientsList = () => {
  const [clients, setClients] = useState([]);

  useEffect(() => {
    const fetchClients = async () => {
      try {
        const response = await axios.get('http://localhost:5000/clients');
        log.debug('Clients data:', response.data);  // Verifica la estructura de los datos aquí
        setClients(response.data);
      } catch (error) {
        log.error('Error fetching clients:', error);
      }
    };

    fetchClients();
  }, []);

  return (
    <Container>
      <h2>Clientes</h2>
      <ListGroup>
        {clients.map((client) => (
          <ListGroup.Item key={client.ClienteId}>
            <strong>ID: {client.ClienteId}</strong><br />
            Nombre: {client.Name}<br />
            Email: {client.Email}<br />
            Teléfono: {client.Phone}
          </ListGroup.Item>
        ))}
      </ListGroup>
    </Container>
  );
};

export default ClientsList;
